        <script>
            document.getElementById('btnShowForm').addEventListener('click', function() {
                document.getElementById('formTransaksi').style.display = 'block';
            });

            function togglePasswordVisibility() {
                const passwordInput = document.getElementById('password');
                const icon = document.getElementById('passIcon');
                if (passwordInput.type === 'password') {
                    passwordInput.type = 'text';
                    icon.textContent = 'visibility_off';
                } else {
                    passwordInput.type = 'password';
                    icon.textContent = 'visibility';
                }
            }

            document.getElementById('btnHideForm').addEventListener('click', function() {
                document.getElementById('formTransaksi').style.display = 'none';
            });

            $(document).ready(function() {
                var logoBase64 =
                    `data:image/png;base64,`
                var table = $('#tabelPetugas').DataTable({
                    "paging": true,
                    "searching": true,
                    "ordering": true,
                    "info": true,
                    "pagingType": "full_numbers",
                    "language": {
                        "search": "",
                        "paginate": {
                            "first": "",
                            "last": "",
                            "previous": "",
                            "next": ""
                        }
                    },
                    "dom": 'Brt<"d-flex justify-content-between align-items-center mt-3"ip>',
                    "buttons": [{
                        extend: 'pdfHtml5',
                        className: 'd-none',
                        title: '',
                        filename: 'Laporan_Transaksi_Pospay_' + new Date().getTime(),
                        orientation: 'landscape',
                        pageSize: 'A4',
                        exportOptions: {
                            columns: [1, 2, 3, 4, 5, 6],
                            format: {
                                body: function(data, row, column, node) {
                                    // Hilangkan inisial/avatar (hanya ambil teks nama di class .fw-bold)
                                    if (column === 0) return $(node).find('.fw-bold').text()
                                        .trim() || data;
                                    // Hilangkan elemen badge pada status
                                    if (column === 5) return $(node).find('.badge').text().trim() ||
                                        data;
                                    return data;
                                }
                            }
                        },
                        customize: function(doc) {
                            // Konfigurasi Margin Dokumen [kiri, atas, kanan, bawah]
                            doc.pageMargins = [40, 120, 40, 60];

                            // Temukan node tabel secara dinamis
                            var tableNode = doc.content.find(node => node.table);

                            // Tambahkan Header Kustom (Gaya Header seperti di Gambar)
                            doc.content.splice(0, 0, {
                                columns: [{
                                        // 1. Logo (Kiri)
                                        image: logoBase64,
                                        width: 50,
                                        margin: [0, 0, 10, 0]
                                    },
                                    {
                                        // 2. Judul (Tengah)
                                        stack: [{
                                                text: 'POSPAY ADMIN TERMINAL',
                                                fontSize: 16,
                                                bold: true,
                                                color: '#0D6EFD',
                                            },
                                            {
                                                text: 'Laporan Transaksi Finansial',
                                                fontSize: 12,
                                                bold: true,
                                                color: '#0D6EFD',
                                            }
                                        ],
                                        width: '*',
                                        alignment: 'left', // Diubah ke kiri agar merapat ke logo
                                        margin: [0, 5, 0,
                                            0
                                        ] // Geser sedikit agar benar-benar di tengah (kompensasi lebar logo)
                                    },
                                    {
                                        // 3. Keterangan Waktu (Kanan)
                                        text: 'Dicetak pada: ' + new Date()
                                            .toLocaleString('id-ID', {
                                                dateStyle: 'full',
                                                timeStyle: 'short'
                                            }),
                                        fontSize: 8,
                                        alignment: 'right',
                                        width: 120,
                                        margin: [0, 10, 0, 0]
                                    }
                                ],
                                margin: [0, 0, 0, 10]
                            }, {
                                // Garis pembatas biru (Horizontal Line)
                                canvas: [{
                                    type: 'line',
                                    x1: 0,
                                    y1: 0,
                                    x2: 760,
                                    y2: 0, // Sesuaikan x2 untuk mode landscape (sekitar 760-800)
                                    lineWidth: 1,
                                    lineColor: '#0D6EFD'
                                }],
                                margin: [0, 0, 0, 20]
                            });

                            if (tableNode) {
                                // Atur lebar kolom agar proporsional di kertas landscape
                                tableNode.table.widths = ['20%', '15%', '15%', '20%', '15%', '15%'];

                                // Styling header kolom tabel
                                var headerRow = tableNode.table.body[0];
                                headerRow.forEach(function(cell) {
                                    cell.fillColor = '#0D6EFD';
                                    cell.color = 'white';
                                    cell.bold = true;
                                    cell.alignment = 'center';
                                    cell.margin = [0, 5, 0, 5];
                                });
                            }
                        }
                    }]
                });

                // Perbaikan Trigger Klik: Menggunakan delegated event yang lebih kuat
                $(document).on('click', '#btnExportPDF', function(e) {
                    e.preventDefault();
                    console.log("Pemicu Export PDF dijalankan...");
                    if (table.button('.buttons-pdf').length > 0) {
                        table.button('.buttons-pdf').trigger();
                    } else {
                        alert("Ekstensi PDF belum siap dimuat.");
                    }
                });

                // --- LOGIKA FILTER (Tetap Sama) ---
                $('#filterSearch').on('keyup', function() {
                    table.search(this.value).draw();
                });
                $('#filterLayanan').on('change', function() {
                    table.column(3).search(this.value).draw();
                });
                $('#filterStatus').on('change', function() {
                    table.column(6).search(this.value).draw();
                });
                $('#filterTanggal').on('change', function() {
                    let rawDate = this.value;
                    if (rawDate) {
                        let parts = rawDate.split('-');
                        table.column(4).search(parts[2] + '-' + parts[1] + '-' + parts[0]).draw();
                    } else {
                        table.column(4).search('').draw();
                    }
                });
                $('#btnClearFilter').on('click', function() {
                    $('input, select').val('');
                    table.search('').columns().search('').draw();
                });
            });

            const nominalInput = document.getElementById('nominal');

            nominalInput.addEventListener('input', function(e) {
                let value = this.value.replace(/\D/g, '');
                this.value = new Intl.NumberFormat('id-ID').format(value);
            });

            //print nota
            @if (session('print_id'))
                Swal.fire({
                    title: 'Berhasil!',
                    text: "{{ session('success') }}",
                    icon: 'success',
                    showCancelButton: true,
                    confirmButtonText: '<span class="material-symbols-outlined">print</span> Cetak Struk',
                    cancelButtonText: 'Tutup',
                    confirmButtonColor: '#0D6EFD',
                }).then((result) => {
                    if (result.isConfirmed) {
                        // Membuka halaman print di tab baru
                        window.open("{{ url('transaksi/print', session('print_id')) }}", "_blank");
                    }
                });
            @endif
        </script>
    @endpush
